import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            costpermile:0,
            iPaid:"",
            gPaid:"",
            miles:"",
        }
        this.handleChange= this.handleChange.bind(this);

    }

    handleChange(event) {

        const {name,value} = event.target
        this.setState({
            [name]: value
        });

    }

    /*handleSubmit(e) {
        e.preventDefault();
        console.log(this.state.costpermile)
        const newItem = {
          ipaid: this.state.iPaid,
          gpaid: this.state.gPaid,
          Miles: this.state.miles
        };
    let final = (parseInt(newItem.ipaid) + parseInt(newItem.gpaid))/parseInt(newItem.Miles);

    this.setState({costpermile: final
    })

    }*/

    render(){

        return(
            <div>
                <h1>
                    Cost Per Mile Calculator
                </h1>
                <form className = "bestCalculator"onSubmit = {this.handleSubmit}>
                    <div>
                        <label>
                            How much have you paid for insurance this year?
                        </label>
                        <input
                        onChange={this.handleChangeiPaid}
                        id = "insurance"
                        type = "number" 
                        name = "iPaid"
                        value = {this.state.iPaid}
                        />
                    </div>



                    <div>
                        <label>
                            How much have you paid for gas this year?
                        </label>
                        <input
                        onChange={this.handleChangegPaid}
                        id = "gas"
                        type = "number" 
                        name = "gPaid"
                        value = {this.state.gPaid}
                        />
                    </div>

                    <div>
                        <label>
                            How many miles have you driven this year?
                        </label>
                        <input
                        onChange={this.handleChangemiles}
                        id = "miles"
                        type = "number" 
                        name = "miles"
                        value = {this.state.miles}
                        />
                    </div>
                    <button className = "button">
                        calculate!
                    </button>



                </form>
                <div className = "cost">
                   Cost per mile: $ { this.state.costpermile.toFixed(2) }
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
