import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';



class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            paidG: 0,
            paidI: 0,
            miles: 0,
            costpermile:0
        }
    }
    calculate(){
        this.setState({
            costpermile: (this.state.paidG + this.state.paidI)/this.state.miles
        })

    }

    handleChange(event){
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    render(){

        return(
            <div>
                <form>
                    <p>
                        How much have you paid for gas this year?
                    </p>
                    <input
                    onChange={this.handleChange} 
                    type = "number"
                    value =  "paidG"

                    />

                    <p>
                        How much have you paid for insurance this year?
                    </p>
                    <input
                    onChange={this.handleChange}
                    type = "number" 
                    value = "paidI"
                    />

                    <p>
                        How many miles have you drive this year?
                    </p>
                    <input
                    onChange={this.handleChange}
                    type = "number"
                    value = "miles"

                    />


                    <button onClick = {(
                        this.calculate
                    )}>
                        calculate!
                    </button>


                </form>
                <div>
                    this is how much it costs per mile: {this.costpermile}
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
