import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            costpermile:0
        }
    }


    handleChange(e) {
        e.preventDefault();
        console.log(e.target.value);
        this.setState({costpermile: e.target.value });

    }

    handleSubmit(e) {
        e.preventDefault();
        const newItem = {
          costpermile: e.target.value

        };

      }

    render(){

        return(
            <div>
                <form onSubmit = {this.handleSubmit}>

                    <label>
                        How much have you paid for insurance this year?
                    </label>
                    <input
                    //onChange={this.handleChange}
                    id = "insurance"
                    onChange = {this.handleChange()}
                    type = "number" 
                    name = "insurance"
                    value = {this.state.costpermile}
                    />

                    <button onClick = {(
                        this.calculate
                    )}>
                        calculate!
                    </button>


                </form>
                <div>
                    this is how much it costs per mile: {this.costpermile}
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
