import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            costpermile:0,
            iPaid:"",
            gPaid:"",
            miles:"",
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleChange(event) {
        const {name,value} = event.target
        this.setState({
            [name]: value,
        });
        console.log("hi");


    }

    handleSubmit(e) {


        e.preventDefault();
        let final = (parseInt(this.state.iPaid) + parseInt(this.state.gPaid))/parseInt(this.state.miles);
        console.log(final);
        this.setState(
            {costpermile: final
        }
        )

    }

    render(){

        return(
            <div>
                <img src = "https://vectorforfree.com/wp-content/uploads/2019/04/BMW_Car_PNG_VectorForFree.jpg"/>
                <div  className = "form" >
                    <h1 className = "header">
                        Cost Per Mile Calculator
                    </h1>
                    <form  onSubmit = {this.handleSubmit}>
                        <div>
                            <label>
                                How much have you paid for insurance this year?
                            </label>
                            <br/>

                            <input
                            className = "text"
                            onChange={this.handleChange}
                            id = "insurance"
                            type = "number" 
                            name = "iPaid"
                            value = {this.state.iPaid}
                            />
                        </div>
                        <br/>



                        <div>
                            <label>
                                How much have you paid for gas this year?
                            </label>

                            <br/>

                            <input
                            className = "text"
                            onChange={this.handleChange}
                            id = "gas"
                            type = "number" 
                            name = "gPaid"
                            value = {this.state.gPaid}
                            />
                        </div>
                        <br/>
                        <div>
                            <label>
                                How many miles have you driven this year?
                            </label>

                            <br/>

                            <input
                            className = "text"
                            onChange={this.handleChange}
                            id = "miles"
                            type = "number" 
                            name = "miles"
                            value = {this.state.miles}
                            />
                        </div>
                        <h1>
                            {(parseInt(this.state.gPaid) + parseInt(this.state.iPaid))/parseInt(this.state.miles)}
                        </h1>
                        <button className = "button">
                            calculate!
                        </button>



                    </form>
                    <div className = "cost">
                    Cost per mile: $ { this.state.costpermile.toFixed(2) }
                    </div>
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
