import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class CustomerInfo extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            question: ""
        }
    }
    render(){
        return(
            <form>
                <label>
                    {this.state.question}
                </label>
            </form>
        );
    }
}

class Calculator extends React.Component{
    render(){
        return(
            <div>
                <CustomerInfo/>
                <CustomerInfo/>
                <CustomerInfo/>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
