import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import './css/bootstrap.min.css'



class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            costpermile:0,
            iPaid:"0",
            gPaid:"0",
            miles:"0",
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleChange(event) {
        const {name,value} = event.target
        this.setState({
            [name]: value,
        });



    }

    handleSubmit(e) {


        e.preventDefault();
        let final = (parseInt(this.state.iPaid) + parseInt(this.state.gPaid))/parseInt(this.state.miles);

        this.setState(
            {costpermile: final
        }
        )

    }

    render(){

        return(
            <div>
                <div>
                    <h1>
                        Cost Per Mile Calculator
                    </h1>
                    <div className = "container">

                            <form onSubmit = {this.handleSubmit}>
                                <div> 
                                    <div className = "col-35">
                                        <label>
                                            How much have you paid for insurance this year?
                                        </label>
                                    </div>


                                    <div className = "col-65">
                                        <input
                                        onChange={this.handleChange}
                                        id = "insurance"
                                        type = "number" 
                                        name = "iPaid"
                                        value = {this.state.iPaid}
                                        />
                                    </div>
                                </div>




                                <div>
                                    <div className = "col-35">
                                        <label>
                                            How much have you paid for gas this year?
                                        </label>
                                    </div>

                                    <div className = "col-65"> 
                                        <input

                                        onChange={this.handleChange}
                                        id = "gas"
                                        type = "number" 
                                        name = "gPaid"
                                        value = {this.state.gPaid}
                                        />
                                    </div>
                                </div>


                                <div>
                                    <div className = "col-35">
                                        <label>
                                            How many miles have you driven this year?
                                        </label>
                                    </div>

                                    <div className = "col-65"> 
                                        <input

                                        onChange={this.handleChange}
                                        id = "miles"
                                        type = "number" 
                                        name = "miles"
                                        value = {this.state.miles}
                                        />
                                    </div>
                                </div>




                                <input className = "button" type = "submit" value = "Calculate!"/>



                            </form>

                        <div className = "answer">
                        Cost per mile: $ { this.state.costpermile.toFixed(2) }
                        </div>
                        <div className = "answer">
                                Current CPM: {parseInt(this.state.miles) !==0? (parseInt(this.state.gPaid) + parseInt(this.state.iPaid))/parseInt(this.state.miles): "Error due to division by 0"}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
