import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            costpermile:0,
            iPaid:0,
            miles:1,
        }
        this.handleChangeiPaid= this.handleChangeiPaid.bind(this);
        this.handleChangemiles= this.handleChangemiles.bind(this);
        this.handleChangegPaid= this.handleChangegPaid.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChangegPaid(e) {

        this.setState({gPaid: e.target.value})
        console.log("gPaid: " +  this.state.gPaid);
    }
    handleChangeiPaid(e) {

        this.setState({iPaid: e.target.value})
        console.log("iPaid: " +  this.state.iPaid);
    }

    handleChangemiles(e) {

        this.setState({miles: e.target.value})
        console.log("miles: " +  this.state.miles);
    }
    handleSubmit(e) {
        e.preventDefault();
        console.log(this.state.costpermile)
        const newItem = {
          ipaid: this.state.iPaid,
          gpaid: this.state.gPaid,
          Miles: this.state.miles
        };
    let final = (parseInt(newItem.ipaid) + parseInt(newItem.gpaid))/parseInt(newItem.Miles);

    this.setState({costpermile: final
    })

      }

    render(){

        return(
            <div>
                <h1>
                    Cost Per Mile Calculator
                </h1>
                <form className = "bestCalculator"onSubmit = {this.handleSubmit}>
                    <div>
                        <label>
                            How much have you paid for insurance this year?
                        </label>
                        <input
                        onChange={this.handleChangeiPaid}
                        id = "insurance"
                        type = "number" 
                        name = "insurance"
                        value = {this.state.iPaid}
                        />
                    </div>



                    <div>
                        <label>
                            How much have you paid for gas this year?
                        </label>
                        <input
                        onChange={this.handleChangegPaid}
                        id = "gas"
                        type = "number" 
                        name = "gas"
                        value = {this.state.gPaid}
                        />
                    </div>

                    <div>
                        <label>
                            How many miles have you driven this year?
                        </label>
                        <input
                        onChange={this.handleChangemiles}
                        id = "miles"
                        type = "number" 
                        name = "miles"
                        value = {this.state.miles}
                        />
                    </div>
                    <button onClick = {(
                        this.calculate
                    )}>
                        calculate!
                    </button>



                </form>
                <div className = "cost">
                   Cost per mile: $ { this.state.costpermile.toFixed(2) }
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
