import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import './css/bootstrap.min.css'



class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            costpermile:0,
            iPaid:"",
            gPaid:"",
            miles:"",
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleChange(event) {
        const {name,value} = event.target
        this.setState({
            [name]: value,
        });



    }

    handleSubmit(e) {


        e.preventDefault();
        let final = (parseInt(this.state.iPaid) + parseInt(this.state.gPaid))/parseInt(this.state.miles);

        this.setState(
            {costpermile: final
        }
        )

    }

    render(){

        return(
            <div>

                <div   >
                    <h1 >
                        Cost Per Mile Calculator
                    </h1>
                    <form  onSubmit = {this.handleSubmit}>
                        <div>


<div class="form-group">
  <label for="usr">How much have you paid for insurance this year?</label>
  <input  class="form-control" onChange={this.handleChange}
                            id = "insurance"
                            type = "number" 
                            name = "iPaid"
                            value = {this.state.iPaid} ></input>
                            </div>

                        </div>
                        <br/>
                        <br/>



                        <div>
                            <label>
                                How much have you paid for gas this year?
                            </label>

                            <br/>

                            <input

                            onChange={this.handleChange}
                            id = "gas"
                            type = "number" 
                            name = "gPaid"
                            value = {this.state.gPaid}
                            />
                        </div>

                        <div>
                            <label>
                                How many miles have you driven this year?
                            </label>



                            <input

                            onChange={this.handleChange}
                            id = "miles"
                            type = "number" 
                            name = "miles"
                            value = {this.state.miles}
                            />
                        </div>
                        <h1>
                            {(parseInt(this.state.gPaid) + parseInt(this.state.iPaid))/parseInt(this.state.miles)}
                        </h1>
                        <button >
                            Calculate!
                        </button>



                    </form>

                    <div >
                    Cost per mile: $ { this.state.costpermile.toFixed(2) }
                    </div>
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
