import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            costpermile:0,

        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(e) {
        console.log(e.target.value);
        this.setState({costpermile: e.target.value });
    }
    handleSubmit(e) {
        e.preventDefault();
        console.log(this.state.costpermile)
        const newItem = {
          costpermile: this.state.costpermile,

        };

      }

    render(){

        return(
            <div>
                <form onSubmit = {this.handleSubmit}>

                    <label>
                        How much have you paid for insurance this year?
                    </label>
                    <input
                    onChange={this.handleChange}
                    id = "insurance"
                    type = "number" 
                    name = "insurance"
                    value = {this.state.iPaid}
                    />

                    <button onClick = {(
                        this.calculate
                    )}>
                        calculate!
                    </button>




                    <label>
                        How much have you paid for gas this year?
                    </label>
                    <input
                    onChange={this.handleChange}
                    id = "gas"
                    type = "number" 
                    name = "insurance"
                    value = {this.state.gPaid}
                    />

                    <button onClick = {(
                        this.calculate
                    )}>
                        calculate!
                    </button>






                </form>
                <div>
                    this is how much it costs per mile: {this.state.costpermile}
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
