import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            costpermile:0,
            iPaid:0,
            miles:0,
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChangeiPaid(e) {

        this.setState({iPaid: e.target.value})
        console.log("iPaid: " +  this.state.iPaid);
    }
    handleSubmit(e) {
        e.preventDefault();
        console.log(this.state.costpermile)
        const newItem = {
          ipaid: this.state.iPaid,
          gpaid: this.state.gPaid,
          Miles: this.state.miles
        };

      }

    render(){

        return(
            <div>
                <form onSubmit = {this.handleSubmit}>

                    <label>
                        How much have you paid for insurance this year?
                    </label>
                    <input
                    onChange={this.handleChangeiPaid}
                    id = "insurance"
                    type = "number" 
                    name = "insurance"
                    value = {this.state.iPaid}
                    />

                    <button onClick = {(
                        this.calculate
                    )}>
                        calculate!
                    </button>










                </form>
                <div>
                    this is how much it costs per mile: {this.state.costpermile}
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
