import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Calculator extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            paidG: 0,
            paidI: 0,
            miles: 0,
            costpermile:0
        }
    }
    calculate(){
        this.setState({
            costpermile:(this.state.paidG + this.state.paidI)/this.state.miles
        })

    }

    handleChange(e) {
        this.setState({costpermile: costpermile + e.target.value });
      }

    render(){

        return(
            <div>
                <form>

                    <p>
                        How much have you paid for insurance this year?
                    </p>
                    <input
                    onChange={this.handleChange}
                    id = "insurance"
                    type = "number" 
                    name = "insurance"
                    value = "paidI"
                    />

                    <button onClick = {(
                        this.calculate
                    )}>
                        calculate!
                    </button>


                </form>
                <div>
                    this is how much it costs per mile: {this.costpermile}
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
  );
