function compare(a,b){
    const nameA = a.Make_Name;
    const nameB = b.Make_Name;
    let comparison = 0;
    if(nameA> nameB){
        comparison = 1;
    }else if(nameA< nameB){
        comparison = -1;
    }
    return comparison;
}

class Calculator extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            costpermile: 0,
            iPaid: "",
            miles: "",
            mait: "",
            api: require('./carmakes.json')["Results"],
            models: [],
            originalPrice: "",
            finalPrice: "",
            tolls: "",
            statecode: "",
            mpg: "",
            subscriptions: "",
            gallon: "",
            typeOfGas: "gasoline",
            priceOfGas: "",
            city: "",
            VIN: "",
            VINAPI: "",
            depreciationValue: "",
            fullcharge: "",
            fullchargeCost: "",
            zipcode: "",
            carMake: "",
            carModel: "",
            isElectric: "",
            carYear: "",
            carBasePrice: "",
            seeOtherCPM: "",

