                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                How much is your car worth now?
                            </Form.Label>
                            <Form.Control
                                placeholder="Enter the current price"
                                onChange={this.handleChange}
                                id="finalPrice"
                                type="number"
                                name="finalPrice"
                                value={this.state.finalPrice}
                            />
                        </Form.Group>
                        <input type="submit"
                        />
                        </Jumbotron>
                    </Form>
                    <br />
                    <h2>See Your CPM in relation to other CPM</h2>
                    <Form.Group>
                        <Form.Label>
                            Enter what information you would like to see
                        </Form.Label>
                        <Form.Control as="select"
                            onChange={this.handleChange}
                            id="seeOtherCPM"
                            type="text"
                            name="seeOtherCPM"
                            value={this.state.seeOtherCPM}
                        >
                            <option name="seeOtherCPM">-Choose one of the below-</option>
                            <option name="seeOtherCPM">Average for 10k miles</option>
                            <option name="seeOtherCPM">Average for 15k miles</option>
                            <option name="seeOtherCPM">Average for 20k miles</option>
                        </Form.Control>

                    </Form.Group>
                    {otherCPM}



            </Container>
        );
    }
}

ReactDOM.render(
    <Calculator />,
    document.getElementById("root")
);
